	},
	["Master Mathias Shaw"] = {
		["Welcome to SI:7 headquarters, $n."] = "f02518f56be86de855c4a5c585841f34"
	},
	["Dungar Longdrink"] = {
		["These great beasts know paths that ye can't find on foot, they'll get ye there fast and maybe show ye something new at the same time."] = "483ae8ef81d9a1fa5470403f505b2d87",
		["Where is it ye would like to go $g lad : lass;? For just a few coin my Gryphons can get ye there faster than even the swiftest horse."] = "6b05ee484d45803ee175e05a07d644f4"
	},
	["Priestess Anetta"] = {
		["I trust the Light is with you, $n. Is there something that I can I help you with?"] = "aebf3948f2b6eeebadd9d114b8e08bba",
		["Do not turn your back on the Light, $c, it may be the one thing that saves you some day."] = "120875f188ac12780f811009b51a783b",
