	["Mountaineer Kadrell"] = {
		["Hello, $n.  Welcome to Thelsamar, and while you're here don't forget to visit our local brewery!"] = "185744ecb6ab01130a75f717debfc573",
		["Welcome to Thelsamar, your home away from home in Loch Modan!$B$BThelsamar is a nice place, but it's not all beer and salted meat for those who answer the call to duty!  If you're willing to face danger in the name of the Alliance, then read the poster outside the Thelsamar branch of the Explorers' League."] = "84a74f16eb6fad3d9b9ac64cef383cac"
	},
	["Wilhelm Strang"] = {
