	},
	["Georgio Bolero"] = {
		["Yes, I've been running this shop for some time now. Business has been pretty good lately. A lot of new adventurers coming through..."] = "191c0730f0b850af3afce8e3beb43593",
		["There's only one person I know of who is more learned in the art of tailoring than myself. Last I heard he could be found in Theramore Isle. He is the person you should seek."] = "9485e8cef81326a7a0116e50049a19ba",
		["You'll need more training before I can share my knowledge with you, I'm afraid. "] = "b35756d26207fcb3c0a6292cafad396c",
		["Good! I can tell that my efforts will not be wasted!"] = "ddcbbdc4dca9d1bfc3f2a254cfd1dc1a"
	},
	["Alexandra Bolero"] = {
