	["Myizz Luckycatch"] = {
		["Your skill exceeds mine, though  I've heard that Old Man Heming in Booty Bay has copies of 'The Bass and You'.  That is sure to help you increase your skill."] = "3bcdd8ed1fc1986116ad0c43c7de1475",
