	["Jocaste"] = {
		["Welcome to the Cenarion Circle, $c."] = "7b9df1f70b5451c09c6a5311f56bf07e",
		["I cannot train a $c such as yourself."] = "9bd1614bca66fd26e163696ba1b6b6fc",
		["Through rigorous retraining I have had to break many students of all they had previously learned so that I might teach them anew.  The service I offer becomes increasingly difficult to perform each time it is done.  It is for that reason that it becomes increasingly expensive with each retraining.  Do you desire that I break you of the talents you have learned?"] = "002e3e2a721f2f5fb8a4b9da6cdeb661"
	},
	["Idriana"] = {
		["Welcome to the Bank of Darnassus. We offer financial accounts and safety deposit boxes for valuable items. Do you already have an account with us $g sir : ma'am;?"] = "72fdda7ff30be23db5fd1e0d05a37089"
	},
	["Astaia"] = {
		["Your skill exceeds mine, though  I've heard that Old Man Heming in Booty Bay has copies of 'The Bass and You'.  That is sure to help you increase your skill."] = "e170ac547f813e355db1e3b75f753815",
		["You will need to find Nat Pagle in Dustwallow Marsh to get any better than you are currently."] = "49c592e9b3c5eeb0c33e65239b4c0b06",
		["I can teach you fishing skills."] = "b4dab18389b060695ecdb043fb33d368"
	},
	["Me'lynn"] = {
		["What a fine day! It inspires me to create a new design."] = "8ad0f749911a3563135d77e9f55d331f",
		["Your talents excel! You are truly inspired. To continue your pursuit of knowledge, speak with Georgio Bolero in Stormwind. He is held in high regard, and will be able to further your instruction."] = "8fde40eee1eab031066dd85909d1ef50",
		["There are basics that must be learned. Trianna is standing nearby, speak with her and return to me later."] = "35b8db7ba4c158e508af94c80ef204f9",
		["You have done well, traveler. I am proud to call you my student!"] = "0fb161fe21a8ff5f560e714f38fb0b44"
	},
	["Ainethil"] = {
		["This potion seems to heal less damage every time I brew it, though I use the same ingredients. There can be only one conclusion: the plants are changing. I cannot say why or into what."] = "13119ff66e549fa7660732b19647f9bd",
		["It is time you moved on to a more advanced teacher. In the Feathermoon Stronghold of Feralas dwells the night elf Kylanna Windwhisper. She has a brilliant mind; doubtless she can train you further."] = "a6416950bbc59cd6baa28946a3f76041",
		["Your skill at brewing potions needs to be greater before you can become one of my students. In the meantime, perhaps Sylvanna Forestmoon or Milla Fairancora can help you, just over there."] = "58b32f3b07654af166e55ee5ae463360",
		["Your education is progressing well. You should be proud of your accomplishments."] = "015468259c43500419a2f50f203dbd3e"
	},
	["Syurna"] = {
		["We are but darkness and shadows. Eternal. Invisible."] = "e8b964bb2f42459fd5d4d58181834b5d",
		["Greetings young rogue. What do you seek?"] = "249236308a24ca006ca2a9251d3eea0a",
		["Messengers from Ravenholdt delivered this letter to me this morning. It is addressed to you, $n."] = "8a0abdcbc600f60a9fcf653afbc414fa",
		["Through rigorous retraining I have had to break many students of all they had previously learned so that I might teach them anew.  The service I offer becomes increasingly difficult to perform each time it is done.  It is for that reason that it becomes increasingly expensive with each retraining.  Do you desire that I break you of the talents you have learned?"] = "002e3e2a721f2f5fb8a4b9da6cdeb661"
	},
	["Elissa Dumas"] = {
		["I do not believe you possess the mental acuity to grasp the nature of portal magic."] = "6169b63082c1481ee1b292a6bd277450",
		["As a mage, you are one of a select group that can conjure a portal to transport you back to this very place.  I can teach you if you are ready."] = "c0b64230899c1f169032e2124bab7479"
	},
	["Grondal Moonbreeze"] = {
		["Needlework requires a steady hand. And patience... above all, patience."] = "afa3c58692e1f910f1511cf81176b4cf",
		["It is time for you to journey to Darnassus and speak to Me'lynn. She will guide you from now on."] = "2b4f7103b433f22f1e0130533daa0976",
		["I would be happy to take you under my wing. Let us begin."] = "a5bb9bf7601a2bba6b4270f85bdd04f4"
	},
	["Laird"] = {
		["Although the coast of Darkshore is more dangerous than that of Teldrassil, many fishers find profit here."] = "40cb7a31eb60d273876ed5e0a23aa52f"
	},
	["Dorion"] = {
		["I cannot train a $c such as yourself."] = "f2c2c7d6984ce15c597fe07b1c728e29",
		["Welcome to the Cenarion Circle, $c.  How may I assist you?"] = "7dd7ee318342731a34bf5316088d4c74",
		["Through rigorous retraining I have had to break many students of all they had previously learned so that I might teach them anew.  The service I offer becomes increasingly difficult to perform each time it is done.  It is for that reason that it becomes increasingly expensive with each retraining.  Do you desire that I break you of the talents you have learned?"] = "30ee103c545f987ab518c70f6d3e640e"
	},
	["Alegorn"] = {
		["Cooking is a life long pursuit and I see we share the same passion!"] = "95be6f4a5e8c06f411a7922aba42f920",
		["Seek out Shandrina in Silverwind Refuge.  She can sell you the 'Expert Cookbook'."] = "9530fe03acd8fd0d529479909397654b",
		["Dirge Quikcleave of Gadgetzan is a master of the culinary arts.  To better yourself you will need to seek him out."] = "39b65bdd01752b5105cbbb2683f8191b",
		["I can teach you how to cook!"] = "d8db5bcf701df43b6392f5a3b85ce404"
	},
	["Dannelor"] = {
		["The esteemed Doctor Gustaf VanHowzen can train you further.  He can be found in Theramore treating the wounded at the barracks."] = "d3686c1236f20aa21b8980c23e1b8adb",
		["I've heard it said that Deneb Walker, holed up in Stromgarde, has a book that is sure to help improve your First Aid techniques."] = "f4ce78e835b2a64f849bbc3c885698a9",
		["I can train you in First Aid techniques."] = "d4d40db0ea32c39f6285731935893eca"
	},
	["Telonis"] = {
		["The grass is soft and lush. The air is fresh, full of light and song. Everything I've seen suggests that Teldrassil is a dream come true. Why, then, is my heart full of foreboding?"] = "4c3e882d5b6e6e4d3050a7dcc19f97b7",
		["You have advanced beyond my skill to teach you. Journey now to Aerie Peak in the Hinterlands and speak with the dwarf Drakk Stonehand. His work is magnificent, far better than my own."] = "7461509c37eb6a03aa5e3875c4eeb8d1",
		["I regret to say that I cannot aid you, $gnoble sir:gentle lady;, until you learn more of working with leather. Speak with Faldron or Darianna here instead. They can teach you the skill you require."] = "5d568f2ee6e5585a3694d234762d64af",
		["Do not try to force your will upon the leather. Rather, let its natural tendencies dictate what form and function it will take. This is not a battle to be won. Work with the leather, not against it."] = "2311acc40d0eada3baa06ec889348fc2"
	},
	["Taladan"] = {
		["It is true I have skills to teach to those who are worthy. Would that be you?"] = "a53ae503ee886bb7a89a674e55487015",
		["You are an exceptional enchanter. Beyond my talents as a teacher, I fear. Seek out Kitta Firewind at the Tower of Azora. Only she can qualify as your instructor now."] = "41da82d3c3fe92c245b041f83fe5f39c",
		["You have not yet learned the basics, wanderer. Have you spoken to my assistant, Lalina Summermoon? Perhaps you should."] = "3179709b6d6763d00fafe6e4d749f3e0",
		["Fine work! You are truly deserving of the knowledge I shall bestow upon you!"] = "3184759a6de00367ad581b70c97d3358"
	},
