	["Race Master Kronkrider"] = {
		["The goblin racer packs a lot of punch, but the gnomes hid a trick or two in their car..."] = "85279dcb34b8519c4aa585d439098471",
		["Who has the fastest car?  Who knows?!?"] = "f8abb3ccf31041b3183d41a1896f28e3",
		["Hello, $c!  Welcome to the races at the Shimmering Flats!"] = "cf552d2d1ba02525e8fcd3e41003294d",
		["Plucky doesn't want Tirth to know he's still in the Shimmering Flats, so he turned himself into a chicken!$B$BIf you find Plucky, beckon him to you.  Maybe he'll come out of hiding."] = "818428d85cc2451411dffed3878e6204",
		["Welcome to the races!  Enjoy your stay, and don't bet all your money in one place!"] = "3fda9ba9ead86b9e29b7d1fc68875623"
	},
	["Goblin Pit Crewman"] = {
		["I heard they were going to try to blow up the gnome's car in the next race!  True or not, I'd stand clear of their pit area."] = "0c2296859db21b24eb9910aa4b0c2053",
		["My money's on the goblins!  And if you have any sense then yours is too!"] = "8a6ae51d27ab952a233cd6f757870376",
		["If I were you, I'd buy a goblin race voucher.  Rooting for the other team is a waste of time."] = "84067fc993444ce72a27b52f4f74bec4",
		["You looking for plucky?  Well, you didn't hear me say this, but... maybe if you ask Kronkrider up in the tower, he'll tell you..."] = "6ca0e5f286f5115ccba86010cc9d19a7",
		["If it weren't for that cursed, gnomish luck, they'd never win a race!"] = "1707d93334e41f8adae340c8be1bc91a",
		["Hah!  Tirth's been looking for him for days, but he doesn't want to be found.  He's chicken!"] = "4cf39f0b48e67e4fb4e5d3538ae49f07",
		["If you ask me, I think the goblins are a shoe in.  We have the tools, and we have the talent!"] = "ef38b09c14f46b5a8dcf5d782c630fd6",
		["Oh, you mean Johnson?  I heard he's in hiding."] = "38a01b6277bccedcf3bd8f0506a44706"
	},
	["Gnome Pit Crewman"] = {
		["My Gizmotic Almanacotron says that we should beat the goblins by twenty seconds more than we did last time!"] = "29cb9f241b67a6bd58f46757f27f6f9e",
		["My son could design a faster car than that clunky goblin junk heap!"] = "5a9bf6255acd34e980aebcd04a7a6def",
		["I installed a new device in our car's matrix regulometer.  That should increase its velocity quotient by a factor of no less than fifteen!"] = "bb2e2ac84fdaea93b4cb5f24ace4d539",
		["Plucky?  I heard race master Kronkrider up on the the tower knows where he is."] = "288dfdbe6b0b08a6f2781e22dc5d8ce0",
		["These races are a great opportunity to show our ingenuity!"] = "88d6d4700d0a9435deac731bbef17f8c",
		["Johnson?  I think he's around here somewhere..."] = "5d227c61a514e3f0b41f912efe022c52",
		["If it weren't for the goblins' inconceivable, dumb luck, they'd crash their car during every race!"] = "413ec8412f752d13ea7d3bc09e39592d",
		["Tirth's not himself these days, and neither is his assistant."] = "329639fd7e8c50ac0b798afe6c0f563e"
	},
	["Parqual Fintallas"] = {
		["It's always nice when someone wishes to speak to me. How are things with you, $c?"] = "b219a709c06746dd4308b42ceae14a51",
		["Now, answer me this, $n:$BWhich creature became the Lich King before spreading the plague across Lordaeron?"] = "f6f7ba02da1200cdb9a4345823966550"
	},
	["Braug Dimspirit"] = {
		["Greetings, $c. Is there something I can help you with?"] = "06ed16ee9dc4b0c6ba6d2eea8e2d58cc",
		["My question to you is this, $c:$BWhich of the Aspects created by the titans became the nefarious creature known as Deathwing?"] = "8a2e3ce00848461a788a1c12e87e9577"
	},
	["High Inquisitor Fairbanks"] = {
		["At last, the curse is lifted. Thank you, hero."] = "3146d1e3ce619ce4be9719884bfd59d3",
		["You mean, you don't know? The sword that you carry on your back - it is known as Ashbringer; named after its original owner."] = "5305da0d8750979d1bc134fec6194486",
		["Aye, the Highlord Mograine: A founder of the original order of the Scarlet Crusade. A knight of unwavering faith and purity; Mograine would be betrayed by his own son and slain by Kel'Thuzad's forces inside Stratholme. It is how I ended up here..."] = "ef7daa42b41400a1595d39af4a6e8762"
	},
