	["Brogun Stoneshield"] = {
		["I am sorry $c, but you need to be more experienced before you can survive in Alterac Valley."] = "f78c3c41a85a94fe125292149c2763de",
		["Aye $g lad : lass; are ye ready to head to Alterac Valley and give the Horde a taste o' what they been asking for?"] = "8b3c666dc6e4c0c98f9d6747a2aea0f8"
	},
	["Hegnar Swiftaxe"] = {
		["Did you bring your text books?  Can I look on with you?"] = "e83e527b69146bb42674faef76761b94",
		["Watch your step in here, $gboy:girl;.  The master sees all..."] = "17086fed84c5d89d563d8bdbaa21ee46",
		["Did you finish yesterday's assignment?  If so, then sit by me!"] = "f56e3e539d77c3bded51cb91595974e9",
		["Did you forget your student's attire?  Do you think fashion is more vital than the Scourge's domination?"] = "b1d9467fbe4dea13716545c2bf6c302f",
		["Be silent!  I cannot afford to miss even a moment of this lecture!"] = "db9ed76ac5a5a639587d8f27fe9a6ec3",
		["We must focus if we are to spawn our dragonflight!"] = "320a21f122e733d1cb4deb8239b5a553",
		["Spend less time rattling and more time listening."] = "3cdaa01e5d3f9b1bf933f46723fe25d1",
		["Be quiet!  Can't you see the master is speaking?"] = "54e82db9c1117db8533f92adc4933717"
	},
	["Fimble Finespindle"] = {
		["Lots to do! Haven't been this busy since my cousin accidentally vaporized all the cloth and leather in Gnomeregan. My, it was drafty for a while! Ah, $n, those were the days...."] = "844d2fb5a4d4d41fdd8a5e1cfc1061ab",
		["Nope, can't help you, I'm afraid. Your skills have mine beat! If you want to learn more, talk to the night elf Telonis in the Craftsmen's Terrace of Darnassus. He's amazing!"] = "db9fa2c2d699dd1ac20a87ea78997fd7",
		["Well, I declare! It's a new student! I'll gladly teach you everything I know once you have a little more skill. Go talk to Gretta Finespindle over there, would you? She'll have you up to speed in no time."] = "10d29ce6d81dc4cf54ce10f28a538a8b",
		["Your skills are coming along nicely. Soon you'll be running a leather shop of your own. I'll be glad for the help, $n. My hands are only so fast, you know? There's so much work to do!"] = "84eac8cc8416941453343ffa518cb882"
	},
