	["Tabetha"] = {
		["Your errand must mean much, $c, to draw you so deep into the marsh..."] = "92da45dc1acf7bf8b75facfe52c3b52c"
	},
	["Magus Tirth"] = {
		["Watch where you're stepping, $c, because I have a polymorph spell with your name on it..."] = "580054d04eb523be430e4750bc42907f"
	},
	["Estelle Gendry"] = {
		["With the sanction of Lord Varimathras, the Deathstalkers' numbers grow. More blades to strike at our enemies when they least expect it."] = "d727a9a6c19bda725fe90de6453f386d"
	},
	["Vizzklick"] = {
		["If tailoring is your thing, Vizzklick will most definitely have your supplies. Take a look around, $n, and let me know if you wish to purchase something!"] = "910ba4b855e200d5675b3e6eabb50a83",
		["Grab your things, $n! I've just gotten word from Nilith Lokrav in the Searing Gorge that the Undermarket has set up operations in the heart of the Cauldron. Find Nilith Lokrav in the Searing Gorge and get the low down!"] = "9b4127810f51260020b2c2688447edd5"
	},
	["Rokar Bladeshadow"] = {
		["What's a young $c like you out visiting an old farmer like me?"] = "9bf55e6c6132c78f689d4a846edbc4fd"
	},
	["Baritanas Skyriver"] = {
		["Where would you like to fly to?"] = "8ee9e2b8b1fbeba82851331d36cb251c"
	},
	["Fahrad"] = {
		["Come to learn from the master eh?"] = "ebd6fd62c633a7ae3b195e93921fd027",
		["Do I look like a $c to you? Now run along before I feel the need to oil my blade... in your blood."] = "398d385a4c5ff3a4608c1d2ae16a7833",
		["Through rigorous retraining I have had to break many students of all they had previously learned so that I might teach them anew.  The service I offer becomes increasingly difficult to perform each time it is done.  It is for that reason that it becomes increasingly expensive with each retraining.  Do you desire that I break you of the talents you have learned?"] = "f6708bdc5d32f47f0eaf4fce7cf2764c"
	},
	["Thalon"] = {
		["Where would you like to fly to?"] = "008491a7c8f31e3556f00e8c3fdc3190"
	},
