	["Ravenholdt Guard"] = {
		["Brash. Master Ravenholdt does not like brash."] = "7c18a505d74d7f81d3aa7e4a1fdefca5",
		["The Syndicate is made up of a bunch of hacks - disorganized, unskilled, and lazy... "] = "2d46251ef009b85c2f7a0d08c926ed85",
		["I believe Simone was looking for you earlier, $n. Have you spoken with her yet?"] = "d519fc238c0d5762c8390aa9c05d3188",
		["Ravenholdt Manor does not recognize you, $c."] = "e414479d7e112a24e4ee47c568f2914a",
		["Greetings, $c. Have you spoken with Fahrad yet?"] = "5fc8a171cfb31942883d032ece558b20",
		["An honor to see you again, $n."] = "0554addc6a4f76eb2e7ded959562d585",
		["Watch your back, stranger."] = "81d62566a55e8b5a5e0eb06e933d7a75",
		["It is good to see you again, $n."] = "f425284ed646293e04e52dadcab348c5",
		["You have truly grown more cunning, $n."] = "21bb13a7f298a61dbf47d64106d4e6ac"
	},
	["Ravenholdt Assassin"] = {
		["Making it out of here without a blade in your back or poison in your veins would be an accomplishment."] = "b56f7d7e17228c7846fd0614b535f631",
		["You should talk to Winstone. I hear he's looking for thieves with your skill."] = "6c7ad021c0414bbc450d1003829fa86b",
		["I really envy you, $n. You do amazing work."] = "d6853e06cead9ccc1834d2216de22df7",
		["You dare set foot inside Ravenholdt Manor, worm?"] = "356d2f6aeda9087735082d9d27eccbf0",
		["You're shaping up nicely, $n. I've heard good things..."] = "e5da524ba41d32170463455cafed48e5",
		["Marvelous. Simply marvelous. You are an inspiration to us all, $n."] = "043d2239097f1a9dd04f652e7f01f075"
