	},
	["Fairbanks Transformed"] = {
		["At last, the curse is lifted. Thank you, hero."] = "60139c1e8304893e783a877f2adadf63",
		["You mean, you don't know? The sword that you carry on your back - it is known as Ashbringer; named after its original owner."] = "aff37574b814f55f881169f5208b9e95",
		["Aye, the Highlord Mograine: A founder of the original order of the Scarlet Crusade. A knight of unwavering faith and purity; Mograine would be betrayed by his own son and slain by Kel'Thuzad's forces inside Stratholme. It is how I ended up here..."] = "4e974e3350b6c509820ed806a61f8cec"
	},
	["Warsong Emissary"] = {
		["Unfortunately you are not yet strong enough to survive in Warsong Gulch, $c."] = "f24e4a79c576d78e3b220d459097ffb8",
		["$C, we have a great need for your help in Warsong Gulch against the Silverwing Sentinels.  And there is even greater honor and reputation with the Warsong Outriders to be gained as well!  Do you have the courage to enter battle?"] = "94e10ef8ca4f4ba7b2ed059da3d417e8"
	},
	["Grizzle Halfmane"] = {
		["I am sorry $c, but you need to be more experienced before you can survive in Alterac Valley."] = "b83ab9c1a9dd85b93121a8a221e21094",
		["The Stormpike Guard must be destroyed!  Ready for Alterac Valley $c?"] = "81ed3ff7e3aa4663fea43677ac077dd8"
	},
	["Thamarian"] = {
		["On the northern dock, you can board a ship that will carry you to Rut'theran Village and Darnassus.  From the southern dock, you can find passage across the Great Sea to Menethil Harbor on Khaz Modan.  Safe journeys to you!"] = "69d07a52e02e6c2a801dd6309215b6e6"
	},
	["Hraug"] = {
		["Want to make your demons more powerful?  It'll cost you, but you've come to the right place."] = "4e1a9fddd86d3d21099d60aa02fea747",
		["You're no warlock and I've no time for you.  Begone!"] = "200fc4a4b57ca0db7de99023c501ea23"
	},
	["Lokhtos Darkbargainer"] = {
		["The Thorium Brotherhood does not waste time on ants. Be gone!"] = "807f545aa510396b39fd74ed72c9abcb",
		["The Thorium Brotherhood possesses the knowledge required to make the sacred arms and armaments of the Fire Lord, but we lack the manpower to obtain the materials required for the crafting.$B$BI, Lokhtos Darkbargainer, have been empowered by the Brotherhood to exchange the exalted recipes for materials gathered from the Molten Core.$B$BPresent your offer, $r."] = "30d968320ab86521f066be7db8986c04",
		["A Sulfuron ingot?!??! Impossible! I must have it! I must! I must!$B$B$R, I am authorized to make you a grand deal for that ingot. Perhaps you would be interested to know the secrets of the Hand of Ragnaros? Sulfuras, idiot! The legendary hammer of the Firelord."] = "ffe6ef04a13e4da40d3147386221790a",
		["In exchange for one Sulfuron Ingot, I am authorized to grant you one copy of the plans to forge a Sulfuron hammer.$B$BNow an adventurer like yourself is probably thinking to $g himself:herself;, 'What would I ever need a hammer like that for when what I already own is far superior?'$B$BWell let me tell you something, $g mister:missy;, there ain't nothing more superior than what the Sulfuron hammer can become!"] = "2d38739dd99aac211a82868d39e682e5",
		["You see, all you gotta do is forge the Sulfuron hammer and find yourself the Eye of Sulfuras. Combine the Eye of Sulfuras with the Sulfuron hammer and it will become Sulfuras! Now I'm not going to bog you down with any technical information as to where or how or when you can get an Eye of Sulfuras. I'm sure you'll figure that stuff out. Just sign this here contract so that our agreement is binding and our business will be done."] = "436fa6a143302d46598f8cf4299ea9d9"
	},
	["Lanie Reed"] = {
		["Thorium Brotherhood or not, if one of them Dark Iron gets near to me they'll taste my mattock up the side of their head!  Now, where can I send ya $G lad : lass;?"] = "f38b9f9eeca159b5ac1ef5dcae9ffdb8"
	}
